<span class="line"><span class="token keyword">SELECT</span> <span class="token operator">*</span> <span class="token keyword">FROM</span> <span class="token keyword">user</span> <span class="token keyword">WHERE</span> <span class="token punctuation">(</span><span class="token punctuation">(</span>name <span class="token operator">=</span> <span class="token string">&#39;李白&#39;</span> <span class="token operator">AND</span> <span class="token keyword">status</span> <span class="token operator">=</span> <span class="token string">&#39;alive&#39;</span><span class="token punctuation">)</span> <span class="token operator">AND</span> <span class="token punctuation">(</span>name <span class="token operator">=</span> <span class="token string">&#39;杜甫&#39;</span> <span class="token operator">AND</span> <span class="token keyword">status</span> <span class="token operator">=</span> <span class="token string">&#39;alive&#39;</span><span class="token punctuation">)</span><span class="token punctuation">)</span></span>
